		ajax.open("GET", sURL, false);
		ajax.send(null); 
		
		var oxml= ajax.responseXML;
		
		var oNodeProducts = oxml.getElementsByTagName('products');	

		document.getElementById('spRight_spSearchLeftTelephone_ddProduct').options.length = 0;
		
		document.getElementById('spRight_spSearchLeftTelephone_ddProduct').options[0] =  new Option('','0');
		var j=1;
		for(var i=0;i<oNodeProducts[0].childNodes.length-1; i+=2)
		{
			document.getElementById('spRight_spSearchLeftTelephone_ddProduct').options[j] =  new Option(oNodeProducts[0].childNodes[i+1].childNodes[0].nodeValue,oNodeProducts[0].childNodes[i].childNodes[0].nodeValue);
			j+=1;
		}
	}
	
	function doredirect(){
		if (document.getElementById("spRight_spSearchLeftTelephone_ddProduct").value!=0)
